import React, {useState} from 'react'
// import style from './index.module.css'

//import komponen ui
import Container from '@material-ui/core/Container'
import Paper from '@material-ui/core/Paper'
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import TextField from '@material-ui/core/TextField'
import Grid from '@material-ui/core/Grid'

//import validasi 
import isEmail from 'validator/lib/isEmail'


//imp react router
import { Link } from 'react-router-dom'

import useStyles from "./style";
export default function Register() {

    const classes = useStyles();

    const [form, setform] = useState({
        email : '',
        password : '',
        ulangi_password :''
    });


    const [error, setError] = useState({
        email : '',
        password : '',
        ulangi_password :''
    });



    const handleChange = e =>{
        setform({
            ...form,
            [e.target.name] : e.target.value
        });
    }

    const validate = ()=>{
        const newError = {...error};

        if (!form.email) {
            newError.email = "Email wajib diisi";
        } else if(!isEmail(form.email)) {
            newError.email = "Email tidak valid";
        }

        if (!form.password) {
            newError.password = "Password wajid diisi";
        }

        if (!form.ulangi_password) {
            newError.ulangi_password = "Ulangi Password wajid diisi";
        } else if(form.ulangi_password !== form.password){
            newError.ulangi_password = "Ulangi Password tidak sama dengan password";
        }

        return newError;
    }

   const handleSubmit = e=>{
        e.preventDefault();
        const findErros = validate();

        if (Object.keys(findErros).some(err => err != '')) {
            setError(findErros);
        }

    }

    console.log(form);
    return (
      <Container maxWidth="xs">
          <Paper className={classes.paper}>
            <Typography
            variant="h5"
            component="h1"
            className={classes.title}>Halaman Pendaftaran</Typography>

            <form onSubmit={handleSubmit} noValidate>

                <TextField 
                    id="email"
                    type="email"
                    name="email"
                    margin="normal"
                    label="Alamat Email"
                    fullWidth
                    required
                    value={form.email}
                    onChange={handleChange}
                    helperText ={error.email}
                />

                <TextField 
                    id="password"
                    type="password"
                    name="password"
                    margin="normal"
                    label="Password"
                    fullWidth
                    required
                    value={form.password}
                    onChange={handleChange}
                    helperText ={error.password}
                />

                <TextField 
                    id="ulangi_password"
                    type="password"
                    name="ulangi_password"
                    margin="normal"
                    label="Ulangi Password"
                    fullWidth
                    required
                    value={form.ulangi_password}
                    onChange={handleChange}
                    helperText ={error.ulangi_password}
                />  

                <Grid container className={classes.buttons}>
                    <Grid item xs>
                        <Button type="submit"
                            color="primary"
                            variant="contained"
                            size="large">Daftar</Button>
                    </Grid>

                    <Grid item>
                        <Button 
                            variant="contained"
                            size="large" component={Link} to="/login">Login</Button>
                    </Grid>
                </Grid>
            </form>

        </Paper>
        </Container>
    )
}
