import React,{useRef, useState} from 'react'

//material -ui 
import TextField from '@material-ui/core/TextField'

//  firebase hook
import { useFirebase } from "../../../components/FirebaseProvider";

//import notifikasi
import {useSnackbar} from 'notistack'

//import validator
import isEmail from 'validator/lib/isEmail'

//import style
import useStyles from './style/pengguna'

export default function Pengguna() {

    const { user } = useFirebase();
    const [error, setError] = useState({
        displayName : '',
        email : ''

    });

    const classes = useStyles();
    const displayNameRef =  useRef();
    const emailRef =  useRef();
    const [isSubmitting, setSubmitting] = useState(false);
    const {enqueueSnackbar} = useSnackbar();
    const saveDisplayName = async (e)=>{
        const displayName = displayNameRef.current.value;
        console.log(displayName)

        if (!displayName) {
            setError({
                displayName : "Nama harus diisi !"
            })
        } else if(displayName !== user.displayName){

            setError({
                displayName : ''
            })

            setSubmitting(true);
            await user.updateProfile({
                displayName
            });
            setSubmitting(false);
            enqueueSnackbar('Data pengguna berhasil diupdate', {variant : 'success'});
        }


    }

    const updateEmail = async (e)=>{
        const email = emailRef.current.value;

        if (!email) {
            setError({
                email : 'Email harus diisi !'
            })
        } else if(!isEmail(email)) {

            setError({
                email : 'Email tidak valid'
            })
        } else if(email !== user.email){


            setError({
                email : ''
            })

            setSubmitting(true);

            try {

                await user.updateEmail(email);
                enqueueSnackbar("Email berhasil diupdate ", {variant:'success'});

            } catch(e){

                let emailError = '';

                switch (e.code) {
                    case 'auth/email-already-in-use':
                        emailError="Email sudah digunakan user lain";
                        break;

                    case 'auth/invalid-email':
                        emailError="Email tidak valid";
                        break;

                     case 'auth/requires-recent-login':
                        emailError="Silahkan logout kemudian login kembali untuk update email";
                        break;

                    default:
                        emailError= e.message;
                        break;
                }

                setError({
                    email : emailError
                })

            }

            setSubmitting(false);
        }
    }

    return (
        <div className={classes.pengaturanPengguna}>
            <TextField 
                id="displayName"
                name="displayName"
                label="Nama"
                defaultValue={user.displayName}
                inputProps={{
                    ref : displayNameRef,
                    onBlur: saveDisplayName,
                }}
                disabled={isSubmitting}
                helperText={error.displayName}
                error={error.displayName ? true : false}
            />

            <TextField 
                id="email"
                name ="email"
                label = "Email"
                defaultValue={user.email}
                inputProps={{
                    ref : emailRef,
                    onBlur: updateEmail,
                }}
                disabled={isSubmitting}
                helperText={error.email}
                error={error.email ? true : false}

            />
        </div>
    )
}
