import React from 'react';
import clsx from 'clsx';
import CssBaseline from '@material-ui/core/CssBaseline';
import Drawer from '@material-ui/core/Drawer';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import SignOutIcon from '@material-ui/icons/ExitToApp';
import HomeIcon from '@material-ui/icons/Home';
import StoreIcon from '@material-ui/icons/Store';
import ShoppingChartIcon from '@material-ui/icons/ShoppingCart';
import SettingIcon from '@material-ui/icons/Settings';

//import react -router -dom
import { Route, Switch } from 'react-router-dom';

// import komponent private
import Pengaturan from './pengaturan';
import Produk from './produk';
import Transaksi from './transaksi';
import Home from './home ';

//import styles 
import useStyles from "./style";

// firebase hook
import {useFirebase} from "../../components/FirebaseProvider";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="#">
        Aplikasi Keuangan
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}



export default function Private() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(true);

  const { auth } = useFirebase();

  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };

 const handleSignOut = (e) => {
     auth.signOut();
 }

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="absolute" className={clsx(classes.appBar, open && classes.appBarShift)}>
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            className={clsx(classes.menuButton, open && classes.menuButtonHidden)}
          >
            <MenuIcon />
          </IconButton>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
            <Switch>
                <Route path="/produk" children="Produk" />
                <Route path="/transaksi" children="Transaksi" />
                <Route path="/pengaturan" children="Pengaturan" />
                <Route children="Home" />
            </Switch>
          </Typography>
          <IconButton color="inherit" onClick={handleSignOut}>

              <SignOutIcon />

          </IconButton>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        classes={{
          paper: clsx(classes.drawerPaper, !open && classes.drawerPaperClose),
        }}
        open={open}
      >
        <div className={classes.toolbarIcon}>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>
            <Route path="/" exact children={({match, history}) => {
                    return  <ListItem
                    button
                    selected = {match ? true : false}
                    onClick={()=>{
                        history.push('/')
                    }}
                    >
                        <ListItemIcon>
                            <HomeIcon />
                        </ListItemIcon>
                        <ListItemText primary="Home"/>
                    </ListItem>
            }} 

            />


           <Route path="/produk" exact children={({match, history}) => {
                    return  <ListItem
                    button
                    selected = {match ? true : false}
                    onClick={()=>{
                        history.push('/produk')
                    }}
                    >
                        <ListItemIcon>
                            <StoreIcon />
                        </ListItemIcon>
                        <ListItemText primary="Produk"/>
                    </ListItem>
            }} 

            />


        <Route path="/transaksi" exact children={({match, history}) => {
                    return  <ListItem
                    button
                    selected = {match ? true : false}
                    onClick={()=>{
                        history.push('/transaksi')
                    }}
                    >
                        <ListItemIcon>
                            <ShoppingChartIcon />
                        </ListItemIcon>
                        <ListItemText primary="Transaksi"/>
                    </ListItem>
            }} 

            />

            <Route path="/pengaturan" exact children={({match, history}) => {
                    return  <ListItem
                    button
                    selected = {match ? true : false}
                    onClick={()=>{
                        history.push('/pengaturan')
                    }}
                    >
                        <ListItemIcon>
                            <SettingIcon />
                        </ListItemIcon>
                        <ListItemText primary="Pengaturan"/>
                    </ListItem>
            }} 

            />


        </List>

      </Drawer>
      <main className={classes.content}>
        <div className={classes.appBarSpacer} />
        <Container maxWidth="lg" className={classes.container}>

        <Switch>
          <Route path="/pengaturan" component={Pengaturan}/>
             <Route path="/produk" component={Produk} />
             <Route path="/transaksi" component={Transaksi} />
             <Route  component={Home} />
        </Switch>

          <Box pt={4}>
            <Copyright />
          </Box>
        </Container>
      </main>
    </div>
  );
}


