import { makeStyles} from '@material-ui/styles'


const useStyles= makeStyles(theme=>({

   fab :{
       position:'absolute',
       bottom :theme.spacing(2),
       right : theme.spacing(2)
   },
   card : {
       display:'flex',
       width: 200
   },
   foto :{
       width : 100
   },
   fotoPlaceholder : {
       width : 150 ,
       alignSelf : 'center',
       textAlign : 'center'
   },
   produkDetails : {
       flex :'100',

   },
   produkActions :{
       flexDirection :'column'
   }


}))


export default useStyles;
