import React ,{useState , useEffect}  from 'react'

//material -ui
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";

// firebase hook
import { useFirebase } from "../../../components/FirebaseProvider";
import { useDocument } from "react-firebase-hooks/firestore";


//import component 
import AppPageLoading from '../../../components/AppLoading';

export default function EditProduk({match}) {

    const {firestore, user} = useFirebase();

    const produkDoc = firestore.doc(`toko/${user.uid}/produk/${match.params.produkId}`);
    const [snapshot , loading] = useDocument(produkDoc);


    const [form, setForm] = useState({
        nama :'',
        sku : '',
        harga : 0,
        stock : 0,
        deskripsi : ''
    });

    const [error, setError] = useState({
        nama :'',
        sku : '',
        harga : '',
        stock:'',
        deskripsi : ''
    });

    const [isSubmitting, setSubmitting] = useState(false);

    useEffect(()=>{
        if(snapshot){
            setForm(snapshot.data());
        }
    }, [snapshot]);

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })

        setError({
            ...error,
            [e.target.name] : ''
        })
    }

    const validate = ()=>{
        const newError = {...error};

        if(!form.nama){
            newError.nama = "Nama harus diisi";

        }

        if(!form.harga){
            newError.harga = "Harga harus diisi";

        }

        if(!form.stock){
            newError.stock = "Stock harus diisi";

        }

        return newError;


    }


    const handleSubmit = async (e) => {
        e.preventDefault();

        const findErrors = validate();

        if(Object.values(findErrors).some(err => err !== '')){

            setError(findErrors);

        }else{

            setSubmitting(true);
            try{

                await produkDoc.set(form,{merge:true});

            } catch(e) {

            }
            setSubmitting(false)

        }

    }

    if(loading){
        return <AppPageLoading />
    }

    return (
        <div>
            <Typography variant="h5" component="h1">Edit Produk: {form.nama}</Typography>
            <Grid container alignItems="center" justiify="center">
                <Grid item xs={12} sm={6}>
                    <form id="produk-form" onSubmit={handleSubmit} noValidate>
                        <TextField 
                            id="nama"
                            name="nama"
                            label="Nama Produk"
                            margin="normal"
                            fullWidth
                            value={form.nama}
                            onChange={handleChange}
                            helperText={error.nama}
                            error={error.nama?true:false}
                            disabled={isSubmitting}
                        />

                        <TextField 
                            id="sku"
                            name="sku"
                            label="SKU Produk"
                            margin="normal"
                            fullWidth
                            value={form.sku}
                            onChange={handleChange}
                            helperText={error.sku}
                            error={error.sku?true:false}
                            disabled={isSubmitting}
                        />  

                        <TextField 
                            id="harga"
                            name="harga"
                            label="Harga Produk"
                            type="number"
                            margin="normal"
                            fullWidth
                            value={form.harga}
                            onChange={handleChange}
                            helperText={error.harga}
                            error={error.harga?true:false}
                            disabled={isSubmitting}
                        />


                    <TextField 
                            id="stock"
                            name="stock"
                            label="Stock Produk"
                            type="number"
                            margin="normal"
                            fullWidth
                            value={form.stock}
                            onChange={handleChange}
                            helperText={error.stock}
                            error={error.stock?true:false}
                            disabled={isSubmitting}
                        />

                    <TextField 
                            id="deskripsi"
                            name="deskripsi"
                            label="Deskripsi Produk"
                            margin="normal"
                            fullWidth
                            multiline
                            rowsMax={3}
                            value={form.deskripsi}
                            onChange={handleChange}
                            helperText={error.deskripsi}
                            error={error.deskripsi?true:false}
                            disabled={isSubmitting}
                        />          
                    </form>
                </Grid>
                <Grid item xs={12} sm={6}>
                    <Typography>
                        Upload Gambar
                    </Typography>
                </Grid>
                <Grid item xs={12}>
                    <Button
                        form="produk-form"
                        type="submit"
                        color="primary" 
                        variant="contained">
                        Simpan
                    </Button>
                </Grid>
            </Grid>
        </div>
    )
}
