import React ,{useState} from 'react'

//material -ui
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";

export default function EditProduk() {

    const [form, setForm] = useState({
        nama :'',
        sku : '',
        harga : 0,
        stock : 0,
        deskripsi : ''
    });

    const [error, setError] = useState({
        nama :'',
        sku : '',
        harga : '',
        stock:'',
        deskripsi : ''
    });

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div>
            <Typography variant="h5" component="h1">Edit Produk: {form.nama}</Typography>
            <Grid container alignItems="center" justiify="center">
                <Grid item xs={12} sm={6}>
                    <form>
                        <TextField 
                            id="nama"
                            name="nama"
                            label="Nama Produk"
                            margin="normal"
                            fullWidth
                            value={form.nama}
                            onChange={handleChange}
                            helperText={error.nama}
                            error={error.nama?true:false}
                        />

                        <TextField 
                            id="sku"
                            name="sku"
                            label="SKU Produk"
                            margin="normal"
                            fullWidth
                            value={form.sku}
                            onChange={handleChange}
                            helperText={error.sku}
                            error={error.sku?true:false}
                        />  

                        <TextField 
                            id="harga"
                            name="harga"
                            label="Harga Produk"
                            type="number"
                            margin="normal"
                            fullWidth
                            value={form.harga}
                            onChange={handleChange}
                            helperText={error.harga}
                            error={error.harga?true:false}
                        />


                    <TextField 
                            id="stock"
                            name="stock"
                            label="Stock Produk"
                            type="number"
                            margin="normal"
                            fullWidth
                            value={form.stock}
                            onChange={handleChange}
                            helperText={error.stock}
                            error={error.stock?true:false}
                        />

                    <TextField 
                            id="deskripsi"
                            name="deskripsi"
                            label="Deskripsi Produk"
                            margin="normal"
                            fullWidth
                            value={form.deskripsi}
                            onChange={handleChange}
                            helperText={error.deskripsi}
                            error={error.deskripsi?true:false}
                        />          
                    </form>
                </Grid>
                <Grid item xs={12} sm={6}>
                    <Typography>
                        Upload Gambar
                    </Typography>
                </Grid>
                <Grid item xs={12}>
                    <Button color="primary" variant="contained">
                        Simpan
                    </Button>
                </Grid>
            </Grid>
        </div>
    )
}
