import React, { useState , useEffect } from 'react'

//import material ui
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

//import style
import useStyles from "./style/toko";

//import validator
import isURL from "validator/lib/isURL";

//import firebase hook
import { useFirebase } from "../../../components/FirebaseProvider";

//import notifikasi
import {useSnackbar} from 'notistack'

//import firebase hook document
import {useDocument} from "react-firebase-hooks/firestore";

export default function Toko() {

    const [form, setForm] = useState({
        nama : '',
        alamat :'',
        telepon :'',
        website : ''
    });

    const [error, setError] = useState({
        nama :'',
        alamat :'',
        telepon :'',
        website : ''
    });


    const [isSubmitting, setSubmiting] = useState(false);

    const classes = useStyles();

    const {enqueueSnackbar} = useSnackbar()

    const {firestore, user}  = useFirebase();

    const tokoDoc = firestore.doc(`toko/${user.uid}`);

    const [snapshot , loading] = useDocument(tokoDoc);

    useEffect(()=>{

        if (snapshot) {
            setForm(snapshot.data());
        }

    },[snapshot])


    const handleChange = (e) =>{
        setForm({
            ...form,
            [e.target.name]:e.target.value
        })
    };

    const validate = () => {
        const newError = {...error};

        if (!form.nama) {
            newError.nama = "Nama wajib diisi";
        }

        if (!form.alamat) {
            newError.alamat = "Alamat wajib diisi";
        }

        if (!form.telepon) {
            newError.telepon = "Telepon wajib diisi";
        }

        if (!form.website) {
            newError.website = "Website wajib diisi";
        } else if (!isURL(form.website)) {
            newError.website = "Website tidak valid";
        }


        return newError;
    }

    const handleSubmit = async  (e) => {
        e.preventDefault();
        const findErrors = validate();

        if (Object.values(findErrors).some(err => err !== '')) {
            setError(findErrors);
        } else {

            setSubmiting(true);
            try{
                await tokoDoc.set(form,{merge:true});
                enqueueSnackbar("Data Toko Berhasil Diupdate",{variant:"success"});

            } catch(e){
                console.log(e.message);
                enqueueSnackbar(e.message,{variant:"error"});
            }
            setSubmiting(false);

        }
    }

    if(loading){
        return <h1>loading ....</h1>
    }

    return (
        <div className={classes.pengaturanToko}>
            <form onSubmit={handleSubmit} noValidate>
                <TextField 
                    id="nama"
                    name="nama"
                    label="Nama Toko"
                    margin="normal"
                    fullWidth
                    required
                    value={form.nama}
                    onChange ={handleChange}
                    error={error.nama?true:false}
                    helperText={error.nama}
                    disabled={isSubmitting}
                />

                <TextField 
                    id="alamat"
                    name="alamat"
                    label="Alamat Toko"
                    margin="normal"
                    multiline
                    rowsMax={3}
                    fullWidth
                    required
                    value={form.alamat}
                    onChange ={handleChange}
                    error={error.alamat?true:false}
                    helperText={error.alamat}
                    disabled={isSubmitting}
                />        

                 <TextField 
                    id="telepon"
                    name="telepon"
                    label="No Telepon Toko"
                    margin="normal"
                    fullWidth
                    required
                    value={form.telepon}
                    onChange ={handleChange}
                    error={error.telepon?true:false}
                    helperText={error.telepon}
                    disabled={isSubmitting}
                />   

                <TextField 
                    id="website"
                    name="website"
                    label="Website Toko"
                    margin="normal"
                    fullWidth
                    required
                    value={form.website}
                    onChange ={handleChange}
                    error={error.website?true:false}
                    helperText={error.website}
                    disabled={isSubmitting}
                />         

                <Button
                 type="submit"
                 className={classes.actionButton}
                 variant="contained"
                 color ="primary"
                 disabled={isSubmitting}
                >
                    Simpan
                </Button>
          </form>
        </div>
    )
}
